// ==== helpers ===============================================================
function val(x) { return (x == null) ? '' : ('' + x); }

function get(path, dflt) {  // safe HL7 getter -> string
    try {
        var v = path.toString();
        return v == null ? (dflt || '') : v;
    } catch (e) {
        return dflt || '';
    }
}

function parseDOB(pid7) { // HL7 TS -> yyyy-mm-dd (or mm/dd/yyyy if you prefer)
    var ts = val(pid7).trim(); // e.g., 19900621 or 199006210830-0500
    if (!ts) return '';
    var y = ts.substring(0, 4);
    var m = ts.substring(4, 6);
    var d = ts.substring(6, 8);
    if (y && m && d) {
        // return mm/dd/yyyy if your API expects DOB_TS like before:
        return m + '/' + d + '/' + y;
    }
    return '';
}

function normalizePhone(xtn) {
    var p = val(xtn).replace(/[^\d+]/g, '');
    // keep leading + if present, otherwise keep digits
    return p || '';
}

function getEmailFromXTN(pid13) {
    // PID.13 is XTN; component 4 is Email (XTN.4) in many feeds; also check repeats
    try {
        // handle repeating PID-13
        if (pid13 && pid13.length != null && typeof pid13.length === 'number') {
            for (var i = 0; i < pid13.length; i++) {
                var email = get(pid13[i]['PID.13.4']);
                if (email && email.indexOf('@') > 0) return email;
            }
        } else {
            var email1 = get(pid13['PID.13.4']);
            if (email1 && email1.indexOf('@') > 0) return email1;
        }
    } catch (e) {}
    return '';
}

function coalesce() {
    for (var i = 0; i < arguments.length; i++) {
        var v = val(arguments[i]).trim();
        if (v) return v;
    }
    return '';
}

function get(path, dflt) {
  try {
    var v = path.toString();
    return (v == null || v === '') ? (dflt || '') : v;
  } catch (e) {
    return dflt || '';
  }
}

function parseTS(ts) {
  // expects YYYYMMDD[HHMM[SS]][±ZZZZ], returns 'YYYY-MM-DDTHH:MM:SS' or ''
  ts = (ts || '').trim();
  if (!ts) return '';
  var y = ts.substring(0,4), m = ts.substring(4,6), d = ts.substring(6,8);
  var hh = ts.length >= 10 ? ts.substring(8,10) : '00';
  var mm = ts.length >= 12 ? ts.substring(10,12) : '00';
  var ss = ts.length >= 14 ? ts.substring(12,14) : '00';
  if (!y || !m || !d) return '';
  return y + '-' + m + '-' + d + 'T' + hh + ':' + mm + ':' + ss;
}
function formatLoc(pv1_3) {
  // PV1-3: PL -> pointOfCare^room^bed^facility^locationStatus
  var poc = get(pv1_3['PV1.3.1']);  // e.g., 3N
  var room = get(pv1_3['PV1.3.2']); // e.g., 312
  var bed = get(pv1_3['PV1.3.3']);  // e.g., B
  var fac = get(pv1_3['PV1.3.4']);  // e.g., NBH
  var typ = get(pv1_3['PV1.3.6']);  // sometimes 'BED'
  var parts = [];
  var left = [poc, room, bed].filter(Boolean).join('-');
  if (left) parts.push(left);
  if (fac) parts.push('@ ' + fac);
  if (typ) parts.push('(' + typ + ')');
  return parts.join(' ');
}
function formatXCN(xcn) {
  // XCN: ID^family^given^middle^^degree^^assigningAuthority
  if (!xcn) return '';
  var id = get(xcn['XCN.1']);
  var fam = get(xcn['XCN.2']);
  var giv = get(xcn['XCN.3']);
  var mid = get(xcn['XCN.4']);
  var deg = get(xcn['XCN.6']);
  var name = [giv, mid, fam].filter(Boolean).join(' ').replace(/\s+/g, ' ').trim();
  var label = name ? name : (fam || giv || '');
  var suffix = [];
  if (deg) suffix.push(deg);
  if (id)  suffix.push('(' + id + ')');
  return (label + (suffix.length ? ' ' + suffix.join(' ') : '')).trim();
}

