    payerName = coalesce(get(in1['IN1.4']['IN1.4.1']), get(in1['IN1.3']['IN1.3.1']));
    // Member/Policy Number commonly in IN1-36
    memberId  = coalesce(get(in1['IN1.36']['IN1.36.1']), get(in1['IN1.2']['IN1.2.1'])); // fall back to plan ID if needed
}

// DOB + Sex
