var isNewPatient = true;
var currentPatient = {};

for (var i = 0; i < getArrayOrXmlLength(patients); i++) {
    // Expect these fields to be present in your cached list
    var pMrn   = val(patients[i].MRN);
    var pAuth  = val(patients[i].AssigningAuthority);
    if (mrn && pMrn === mrn && assigningAuthority && pAuth === assigningAuthority) {
        isNewPatient = false;
        currentPatient = patients[i];
        break;
    }
}

//// If MRN/authority lookup failed, fall back to name match (legacy behavior)
if (isNewPatient) {
    for (var j = 0; j < getArrayOrXmlLength(patients); j++) {
        var name = val(patients[j].GivenName) + " " + val(patients[j].FamilyName);
        if (patientName && patientName === name.trim()) {
            isNewPatient = false;
            currentPatient = patients[j];
            break;
        }
    }
}

