/**
	This function gets getEnvironmentConfig() JSON objects and set values needed for the channels and return a new JSON.
	@return {String} return file config JSON
*/
function getEnv() {
    let env = getEnvironmentConfig();
    let envJson = {};
    envJson.client = env.client;
    envJson.emr = env.emr;
    envJson.sendingApplication = env.sendingApplication;

    envJson.archiveDirectory = env.archiveDirectory;
    envJson.inboundDirectory = env.inboundDirectory;
    envJson.archiveDirectory = env.archiveDirectory;
    envJson.documentDirectory = env.documentDirectory;
    envJson.outboundDirectory = env.outboundDirectory;
    const dateString = DateUtil.getCurrentDate("YYYYMMddHHmmss");
    return envJson;
}

/**
	This function parses the config map JSON file from settings.
	@return {String} return file config JSON
*/
function getEnvironmentConfig(name) {
	const configuration = configurationMap.get(name);
	if (this.isEmpty(configuration)) {
		throw "An error has occured while reading the config file ERROR: " + e;
     }
     return JSON.parse(configuration);
}

/**
	This function creates a directory.

	@param {String} directory - directory to be created
	@return {String} return function status
*/
function createDirectory(directory) {
    try {
        const create = Packages.java.lang.Runtime.getRuntime();
        create.exec('mkdir -p ' + directory);
        create.exec('chmod 0770 ' + directory);
    } catch (e) {
        return "An error has occurred while creating directory " + directory + "; error:" + e;
    }
    return "Directory (" + directory + ") successfully created"
}

/**
	this function delete a file.

	@param {String} FilePath - file path to be deleted
	@return {String} return function status
*/
function deleteFile(FilePath) {
    try {

        importPackage(Packages.ij.File);
        const fileToDelete = new java.io.File(FilePath)

        if (fileToDelete.exists) {
            fileToDelete.delete();
            return FilePath + " has been deleted.";
        }

    } catch (e) {
        return e;
    }

}

/**
	This function encoded file using base64.

	@param {String} file - file path to be encoded
	@return {String} return Encoded64 file 
*/
function fileEncodedBase64(file) {
        const fileRead = FileUtil.readBytes(file);
        return FileUtil.encode(fileRead);;

}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function moveFile(inboundDirectory , outboundDirectory, fileName) {
    try {

        importPackage(Packages.ij.File);
        const _file = new java.io.File(inboundDirectory + fileName)

        if (_file.exists) {
            _file.renameTo(new File(outboundDirectory + fileName));
            return fileName + " has been moved.";
        }

    } catch (e) {
        return e;
    }
}

/**
	This function saves string to file.

	@param {String} filename - name of the new file
	@param {String} directory - directory of the new file 
	@param {String} stringData - string to be comverted to file
	@param {String} append - option to apend or not
	@return {String} return new file 
*/
function saveStringToFile(filename, directory, stringData, append) {

    try {
        importPackage(Packages.ij.File);
        org.apache.commons.io.FileUtils.write(new java.io.File(directory + filename), stringData, append);
    } catch (e) {
        return "An error has occured while saving file (" + filename + "); error:" + e;
    }
}

/**
This function formats date to standard ScriptSender date format (Aranya .. test test test1)

	@param {String} arg1 - date to be formated
	@return {String} return formated date
*/
function scriptSenderDateFormat(date) {
        let newdate;
        if (this.isNotEmpty(date)) {
            if (date.length === 8) {
                newdate = date + '000000'
            }
        }
        return newdate;

}

/**
	This function create a Standard ScriptSender file name

	@return {String} return formated file name
*/
function scriptSenderfileNameFormat() {
        const dateString = DateUtil.getCurrentDate("YYYY/MM/dd/HH/mm/ss/SS");
        const yearString = DateUtil.getCurrentDate("YYYY");
        const monthString = DateUtil.getCurrentDate("MM");
        const dayString = DateUtil.getCurrentDate("dd");
        const hourString = DateUtil.getCurrentDate("HH");
        const minuteString = DateUtil.getCurrentDate("mm");
        const secondString = DateUtil.getCurrentDate("SS");

        const fileName = yearString + "_" + monthString + "_" + dayString + "_" + hourString + "_" + minuteString + "_" + secondString;
        return fileName;
}

/**
	This function deletes empty segments from an HL7 message

	@param {String} data - HL7 message
	@return {String} return new HL7 message with non-empty segments
*/
function deleteEmptySegments(data) {
    for (let i = 0; i < data.length; i++) {
        const seg = data[i];
        if (this.isNotEmpty(seg)) {
            let emptySegmentFound = false;
            for (let j = 0; j < seg.length; j++) {
                const sec = seg[j];
                if (this.isNotEmpty(sec)) {
                    emptySegmentFound = true;
                    //break;
                }
            }
            ///const name = seg.localName().toString();
            if (emptySegmentFound === true) {
                delete(data.children()[seg.childIndex()])
            }
        }else{
        	return;
        }
    }
    return data;
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
// Build "Hernandez, Maria J, MD (12345)" from OBX.5 components
function formatDoctorFromObx5(obx) {
    var root = obx['OBX.5'];
    if (!root) return null;

    // Some feeds put plain text in 5.1 (e.g., "None recorded")
    var first = getComp(obx, 'OBX.5.1');
    if (/^none\s+recorded$/i.test(first)) return 'None recorded';

    var id     = first;                    // OBX.5.1 (ID)
    var family = getComp(obx, 'OBX.5.2');  // last
    var given  = getComp(obx, 'OBX.5.3');  // first
    var middle = getComp(obx, 'OBX.5.4');  // middle
    var suffix = getComp(obx, 'OBX.5.5');  // suffix
    var degree = getComp(obx, 'OBX.5.6');  // degree (MD/DO)
    // OBX.5.8 often holds assigning authority ("NPI"), not needed in pretty print

    // If only 5.1 is present and it's not "None recorded", just return it
    var hasNameBits = family || given || middle || suffix || degree;
    if (!hasNameBits && id && !/^\d+$/.test(id)) {
        return id; // it's already a text name
    }

    var name = '';
    if (family) name += family;
    if (given)  name += (name ? ', ' : '') + given;
    if (middle) name += ' ' + middle;
    if (suffix) name += ', ' + suffix;
    if (degree) name += ', ' + degree;
    if (id)     name += ' (' + id + ')';

    return name || null;
}

/**
 * Parse an XCN like "12345^Hernandez^Maria^J^^MD^^NPI" into a neat string:
 * "Hernandez, Maria J, MD (12345)". If it's "None recorded", returns that.
 */
function formatXCN(xcn) {
    if (!xcn) return null;
    var s = String(xcn).trim();
    if (s === '' || /^none\s+recorded$/i.test(s)) return 'None recorded';

    var parts = s.split('^');
    var id       = parts[0] || '';
    var family   = parts[1] || '';
    var given    = parts[2] || '';
    var middle   = parts[3] || '';
    var suffix   = parts[4] || '';
    var degree   = parts[5] || '';

    var name = (family ? family : '');
    if (given)  name += (name ? ', ' : '') + given;
    if (middle) name += ' ' + middle;
    if (suffix) name += ', ' + suffix;
    if (degree) name += (degree ? ', ' + degree : '');

    if (id) name += ' (' + id + ')';
    return name || s; // fallback to raw if all else empty
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function formatXCNFromObx5(obx) {
  // Some feeds put plain text in 5.1 (e.g., "None recorded")
  var first = gx(obx, 'OBX.5.1');
  if (/^none\s+recorded$/i.test(first)) return 'None recorded';

  var id     = first;                 // OBX.5.1
  var family = gx(obx, 'OBX.5.2');    // last
  var given  = gx(obx, 'OBX.5.3');    // first
  var middle = gx(obx, 'OBX.5.4');    // middle
  var suffix = gx(obx, 'OBX.5.5');    // suffix
  var degree = gx(obx, 'OBX.5.6');    // MD/DO
  // OBX.5.8 often "NPI" (assigning authority) — not needed for display

  // If only 5.1 exists and looks like a name (not just digits), return it
  if (!(family || given || middle || suffix || degree) && id && !/^\d+$/.test(id)) return id;

  var name = '';
  if (family) name += family;
  if (given)  name += (name ? ', ' : '') + given;
  if (middle) name += ' ' + middle;
  if (suffix) name += ', ' + suffix;
  if (degree) name += ', ' + degree;
  if (id)     name += ' (' + id + ')';

  return name || null;
}

// Read a text value from a component safely (returns "" if missing)
function getComp(node, compPath) {
    try {
        var n = node;
        var parts = compPath.split('.');
        for (var i = 0; i < parts.length; i++) {
            n = n[parts[i]];
            if (!n) return '';
        }
        var s = String(n);
        return s != null ? s.trim() : '';
    } catch (e) {
        return '';
    }
}

/**
	This function comverts an HL7 segment to an array

	@param {String} data - data HL7 segment
	@param {Object} field - Field from HL7 segment
 	@return {String} return Array
*/
function getHL7Array(data, field) {
        let hl7Result = [];
        if (this.isNotEmpty(field)) {
            for (let i = 0; i < data.length; i++) {
                hl7Result.push(data[i][field].toString());
            }

            if (hl7Result.length > 1) {
                return hl7Result;
            } else {
                return data[field].toString();
            }
        }
        return data;
}

/**
	This function gets sub-Segment array of an HL7 segment

	@param {Object} seg - HL7 segment
	@return {Json} return Json array
*/
function getSubSegmentArray(seg) {
    let children = [];
    let childCount = seg.children().length;

    if (childCount > 0 ) {

    		for (let i = 0; i < childCount; i++) {
        		const child = seg.children()[i];
        		children.push(child.toString());
    		}
    } else {
    	   children = "";
        for (let j = 0; j < seg.children().length; j++) {
            const grandChild = seg.children()[j];
            children = grandChild.toString();
        }
    }
    return children;
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function gx(root, path) {
  // Safe getter for HL7 XML: tries multiple path styles (CE.1 vs OBX.3.1)
  try {
    var node = root, parts = path.split('.');
    for (var i = 0; i < parts.length; i++) {
      node = node[parts[i]];
      if (!node) return '';
    }
    var s = String(node);
    return s != null ? s.trim() : '';
  } catch (e) { return ''; }
}

/**
 * Loop through all OBX segments and map:
 *  - Note (LOINC 18842-5 Discharge summary -> OBX-5)
 *  - AdmittingDoctor (ADM_DOC -> OBX-5 XCN)
 *  - ConsultingDoctor (CONS_DOC -> OBX-5 XCN or "None recorded")
 *  - DischargeDoctor (DIS_DOC -> OBX-5 XCN)
 */
function mapDischargeFromOBX(notes) {
    // clear / initialize (optional — comment out if you prefer to keep prior values)
    channelMap.put('Note', null);
    channelMap.put('AdmittingDoctor', null);
    channelMap.put('ConsultingDoctor', null);
    channelMap.put('DischargeDoctor', null);

    // msg.OBX is a repeating segment list in Mirth’s HL7 XML
    var count = (notes.OBX ? notes.OBX.length() : 0);

    for (var i = 0; i < count; i++) {
        var obx = notes.OBX[i];

        // OBX-3 (CE) – observation identifier
        var idCE1 = (obx['OBX.3']['OBX.3.1'] ? String(obx['OBX.3']['OBX.3.1']) : '').trim(); // code
        var idCE2 = (obx['OBX.3']['OBX.3.2'] ? String(obx['OBX.3']['OBX.3.2']) : '').trim(); // text
        // OBX-5 – observation value (for TX/NM/etc. we read as string)
        var val   = (obx['OBX.5'] ? String(obx['OBX.5']) : '').trim();

        if (!idCE1 && !idCE2) continue;

        // Discharge Note (LOINC 18842-5 or text contains "Discharge summary")
        if (idCE1 === '18842-5' || /discharge\s*summary/i.test(idCE2)) {
            // If multiple OBX for notes, join with a blank line
            var currentNote = channelMap.get('Note');
            if (currentNote && String(currentNote).trim() !== '') {
                channelMap.put('Note', String(currentNote) + '\n\n' + val);
            } else {
                channelMap.put('Note', val);
            }
            continue;
        }

        // Admitting doctor
        if (idCE1 === 'ADM_DOC' || /admitting\s*doctor/i.test(idCE2)) {
            channelMap.put('AdmittingDoctor', formatXCN(val));
            continue;
        }

        // Consulting doctor
        if (idCE1 === 'CONS_DOC' || /consulting\s*doctor/i.test(idCE2)) {
            channelMap.put('ConsultingDoctor', formatXCN(val));
            continue;
        }

        // Discharge doctor
        if (idCE1 === 'DIS_DOC' || /discharge\s*doctor/i.test(idCE2)) {
            channelMap.put('DischargeDoctor', formatXCN(val));
            continue;
        }
    }

    // Optional: if consulting is still blank and OBX said "None recorded", set explicitly
    if (!channelMap.get('ConsultingDoctor')) {
        // leave as null; consumer can treat null as "unknown"
    }
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function mapDischargeFromOBX_clean(root) {
  // init/clear
  channelMap.put('Note', null);
  channelMap.put('AdmittingDoctor', null);
  channelMap.put('ConsultingDoctor', null);
  channelMap.put('DischargeDoctor', null);

  var list = root.OBX ? root.OBX : msg.OBX; // allow passing msg or a subnode
  var count = list ? list.length() : 0;

  for (var i = 0; i < count; i++) {
    var obx = list[i];
    var code = obxIdCode(obx);
    var text = obxIdText(obx);

    // NOTE (18842-5 Discharge summary). For TX, value is in OBX.5.1
    if (code === '18842-5' || /discharge\s*summary/i.test(text)) {
      var noteTxt = gx(obx, 'OBX.5.TX.1') || gx(obx, 'OBX.5.1');
      if (noteTxt) {
        var cur = String(channelMap.get('Note') || '');
        channelMap.put('Note', cur ? (cur + '\n\n' + noteTxt) : noteTxt);
      }
      continue;
    }

    // Admitting doctor
    if (code === 'ADM_DOC' || /admitting\s*doctor/i.test(text)) {
      channelMap.put('AdmittingDoctor', formatXCNFromObx5(obx));
      continue;
    }

    // Consulting doctor
    if (code === 'CONS_DOC' || /consulting\s*doctor/i.test(text)) {
      channelMap.put('ConsultingDoctor', formatXCNFromObx5(obx));
      continue;
    }

    // Discharge doctor
    if (code === 'DIS_DOC' || /discharge\s*doctor/i.test(text)) {
      channelMap.put('DischargeDoctor', formatXCNFromObx5(obx));
      continue;
    }
  }
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
// Extract discharge note and doctors into channelMap
function mapDischargeFromOBX_components(notes) {
    channelMap.put('Note', null);
    channelMap.put('AdmittingDoctor', null);
    channelMap.put('ConsultingDoctor', null);
    channelMap.put('DischargeDoctor', null);

    var total = (notes.OBX ? notes.OBX.length() : 0);
    for (var i = 0; i < total; i++) {
        var obx = notes.OBX[i];
        var id1 = getComp(obx, 'OBX.3.1'); // code ['OBX.3']['OBX.3.1']
        var id2 = getComp(obx, 'OBX.3.2'); // text
//        var id1 = (obx['OBX.3']['OBX.3.1'] ? String(obx['OBX.3']['OBX.3.1']) : '').trim(); // code
//        var id2 = (obx['OBX.3']['OBX.3.2'] ? String(obx['OBX.3']['OBX.3.2']) : '').trim(); // text

        // NOTE (LOINC 18842-5 or text "Discharge summary") -> OBX.5.1
        if (id1 === '18842-5' || /discharge\s*summary/i.test(id2)) {
            var t = getComp(obx, 'OBX.5.1');
            if (t) {
                var cur = String(channelMap.get('Note') || '');
                channelMap.put('Note', cur ? (cur + '\n\n' + t) : t);
            }
            continue;
        }

        // Admitting doctor (ADM_DOC)
        if (id1 === 'ADM_DOC' || /admitting\s*doctor/i.test(id2)) {
            channelMap.put('AdmittingDoctor', formatDoctorFromObx5(obx));
            continue;
        }

        // Consulting doctor (CONS_DOC)
        if (id1 === 'CONS_DOC' || /consulting\s*doctor/i.test(id2)) {
            channelMap.put('ConsultingDoctor', formatDoctorFromObx5(obx));
            continue;
        }

        // Discharge doctor (DIS_DOC)
        if (id1 === 'DIS_DOC' || /discharge\s*doctor/i.test(id2)) {
            channelMap.put('DischargeDoctor', formatDoctorFromObx5(obx));
            continue;
        }
    }
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
// Safe getter for HL7 XML like OBX.3.1 or OBX.5.2
function gx(node, path) {
    try {
        var n = node;
        var parts = path.split('.');
        for (var i = 0; i < parts.length; i++) {
            n = n[parts[i]];
            if (!n) return '';
        }
        var s = String(n);
        return s != null ? s.trim() : '';
    } catch (e) { return ''; }
}

// Pretty print doctor from OBX.5 components
function formatDoctor(obx) {
    var v1 = gx(obx, 'OBX.5.1');
    if (/^none\s+recorded$/i.test(v1)) return 'None recorded';

    var id     = v1;                    // id (or sometimes free text)
    var family = gx(obx, 'OBX.5.2');    // last
    var given  = gx(obx, 'OBX.5.3');    // first
    var middle = gx(obx, 'OBX.5.4');    // middle
    var suffix = gx(obx, 'OBX.5.5');    // suffix
    var degree = gx(obx, 'OBX.5.6');    // MD/DO

    // If only OBX.5.1 exists and it's not just digits, treat it as a preformatted name
    if (!(family || given || middle || suffix || degree) && id && !/^\d+$/.test(id)) {
        return id;
    }

    var name = '';
    if (family) name += family;
    if (given)  name += (name ? ', ' : '') + given;
    if (middle) name += ' ' + middle;
    if (suffix) name += ', ' + suffix;
    if (degree) name += ', ' + degree;
    if (id)     name += ' (' + id + ')';

    return name || null;
}

function mapDischargeOBX2(root) {
    // init/clear
    channelMap.put('Note', null);
    channelMap.put('AdmittingDoctor', null);
    channelMap.put('ConsultingDoctor', null);
    channelMap.put('DischargeDoctor', null);

    var list = (root && root.OBX) ? root.OBX : (msg.OBX || null);
    var count = list ? list.length() : 0;

    for (var i = 0; i < count; i++) {
        var obx = list[i];
        var code = gx(obx, 'OBX.3.1'); // e.g., 18842-5, ADM_DOC, CONS_DOC, DIS_DOC
        var text = gx(obx, 'OBX.3.2'); // description

        // NOTE: Discharge summary (TX) lives in OBX.5.1
        if (code === '18842-5' || /discharge\s*summary/i.test(text)) {
            var t = gx(obx, 'OBX.5.1');
            if (t) {
                var cur = String(channelMap.get('Note') || '');
                channelMap.put('Note', cur ? (cur + '\n\n' + t) : t);
            }
            continue;
        }

        if (code === 'ADM_DOC' || /admitting\s*doctor/i.test(text)) {
            channelMap.put('AdmittingDoctor', formatDoctor(obx));
            continue;
        }
        if (code === 'CONS_DOC' || /consulting\s*doctor/i.test(text)) {
            channelMap.put('ConsultingDoctor', formatDoctor(obx));
            continue;
        }
        if (code === 'DIS_DOC' || /discharge\s*doctor/i.test(text)) {
            channelMap.put('DischargeDoctor', formatDoctor(obx));
            continue;
        }
    }
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function obxIdCode(obx) {
  return gx(obx, 'OBX.3.CE.1') || gx(obx, 'OBX.3.1');
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function obxIdText(obx) {
  return gx(obx, 'OBX.3.CE.2') || gx(obx, 'OBX.3.2');
}

/**
	This function stripEmptyNodes from xml

	@param {String} node - xml node
	@param {String} stripWhitespaceNodes - White space
	@param {String} onlyDeleteTrailing - only Delete Trailing
	@return {String} return new xml
*/
function stripEmptyNodes(node, stripWhitespaceNodes, onlyDeleteTrailing) {
        let nonEmptyFound = false;
        for (let childIndex = node.children().length() - 1; childIndex >= 0; childIndex--) {
            const child = node.children()[childIndex];
            stripEmptyNodes(child, stripWhitespaceNodes, onlyDeleteTrailing);
            if (stripWhitespaceNodes && !child.toString().trim() || !stripWhitespaceNodes && !child.toString())
                if (!nonEmptyFound || !onlyDeleteTrailing)
                    delete node.children()[childIndex];
                else
                    node.children()[childIndex] = new XML('<' + child.name().toString() + '/>');
            else
                nonEmptyFound = true;
        }

}

/**
	This function parses a HL7 segment to Json

	@param {String} procedure_code - procedure_code from HL7 segment
	@param {String} RIS_ID - RIS_ID from HL7 segment
	@param {String} EMR_ID - EMR_ID from HL7 segment
	@param {String} PACS_accession_number - PACS_accession_number from HL7 segment
	@param {String} priority - priority from HL7 segment

	@param {String} modality - modality from HL7 segment
	@param {String} DateOfRequest - DateOfRequest from HL7 segment
	@param {String} DateOfObservation - DateOfObservation from HL7 segment
	@param {String} DateOfEncounter - DateOfEncounter from HL7 segment
	@param {String} study - study from HL7 segment

	@param {String} reasonForExam - reasonForExam from HL7 segment
	@param {String} approvingProvider - approvingProvider from HL7 segment
	@param {String} comment - comment from HL7 segment
	@param {Array} MultiDiagnosisList - MultiDiagnosisList is array list from HL7 segment
	@param {String} status - status from HL7 segment
	@return {String} return Json object
*/
function parseExams(procedure_code, RIS_ID, EMR_ID, PACS_accession_number, priority, modality, dateOfRequest, dateOfObservation, dateOfEncounter, study, reasonForExam, approvingProvider, comment, multiDiagnosisList) {
        let prior = "";
        if (this.isNotEmpty(priority)) {
            prior = priority;
        }
        if(this.isNotEmpty(multiDiagnosisList)){
        	 return {
        		"procedure_code":procedure_code,
        		"EMR_ID":EMR_ID,
        		"RIS_ID":RIS_ID,
        		"PACS_accession_number":PACS_accession_number,
        		"priority":prior,
        		"modality":modality,
        		"DateOfEncounter":dateOfEncounter,
        		"DateOfObservation":dateOfObservation,
        		"DateOfRequest":dateOfRequest,
        		"study":study,
        		"reasonForExam":reasonForExam,
        		"approvingProvider":approvingProvider,
        		"comment":comment,
        		"MultiDiagnosisList":multiDiagnosisList
        };

        }  
        	return {
        		"procedure_code":procedure_code,
        		"EMR_ID":EMR_ID,
        		"RIS_ID":RIS_ID,
        		"PACS_accession_number":PACS_accession_number,
        		"priority":priority,
        		"modality":modality,
        		"DateOfEncounter":dateOfEncounter,
        		"DateOfObservation":dateOfObservation,
        		"DateOfRequest":dateOfRequest,
        		"study":study,
        		"reasonForExam":reasonForExam,
        		"approvingProvider":approvingProvider,
        		"comment":comment    
        };
}

