function getPatiens() {
// ---- DB config (keep secrets out of code; pull from Global Map / Deployed props) ----
var driver = 'com.microsoft.sqlserver.jdbc.SQLServerDriver';
var url =
  'jdbc:sqlserver://he-sql-dev-eus2.database.windows.net:1433;' +
  'database=he-healthcare-db;' +                      // or databaseName=...
  'encrypt=true;trustServerCertificate=false;' +
  'hostNameInCertificate=*.database.windows.net;' +
  'loginTimeout=30;';
//User ID=headmin;Password=HealthExt2025!
var user = String(globalMap.get('HE_DB_USER') || 'headmin');
var pass = String(globalMap.get('HE_DB_PASS') || 'HealthExt2025!');

// ---- Open connection using the 4-arg overload ----
var db = null;
try {
    db = DatabaseConnectionFactory.createDatabaseConnection(driver, url, user, pass);

    // EXAMPLE: parameterized query
    var sql = "SELECT * FROM dbo.Patient WHERE Id = ?";
    var params = [ String(channelMap.get('patientId') || '4') ];

    var rs = db.executeCachedQuery(sql, params);  // returns CachedRowSet
    if (rs.next()) {
        var pid  = rs.getString('Id');
        var fn   = rs.getString('FirstName');
        var ln   = rs.getString('LastName');
        var dob  = rs.getString('DateOfBirth');

        channelMap.put('dbPatientId', pid);
        channelMap.put('dbFirstName', fn);
        channelMap.put('dbLastName',  ln);
        channelMap.put('dbDOB',       dob);
    } else {
        logger.warn('No patient row found for Id=' + params[0]);
    }
} catch (e) {
    logger.error('DB error: ' + e);
    throw e; // or handle as needed
} finally {
    if (db) try { db.close(); } catch (ignore) {}
}

}

