// ===== helpers ==============================================================
function get(path, dflt) {
  try {
    var v = path.toString();
    return (v == null || v === '') ? (dflt || '') : v;
  } catch (e) {
    return dflt || '';
  }
}
function parseTS(ts) {
  // HL7 TS → 'YYYY-MM-DDTHH:MM:SS'
  ts = (ts || '').trim();
  if (!ts) return '';
  var y = ts.substring(0,4), m = ts.substring(4,6), d = ts.substring(6,8);
  var hh = ts.length >= 10 ? ts.substring(8,10) : '00';
  var mm = ts.length >= 12 ? ts.substring(10,12) : '00';
  var ss = ts.length >= 14 ? ts.substring(12,14) : '00';
  if (!y || !m || !d) return '';
  return y + '-' + m + '-' + d + 'T' + hh + ':' + mm + ':' + ss;
}
function formatLoc(pv1_3) {
  // PV1-3: pointOfCare^room^bed^facility^^type
  var poc = get(pv1_3['PV1.3.1']);
  var room = get(pv1_3['PV1.3.2']);
  var bed  = get(pv1_3['PV1.3.3']);
  var fac  = get(pv1_3['PV1.3.4']);
  var typ  = get(pv1_3['PV1.3.6']);
  var left = [poc, room, bed].filter(Boolean).join('-');
  var parts = [];
  if (left) parts.push(left);
  if (fac) parts.push('@ ' + fac);
  if (typ) parts.push('(' + typ + ')');
  return parts.join(' ');
}
function formatXCN(xcn) {
  if (!xcn) return '';
  var id  = get(xcn['XCN.1']);
  var fam = get(xcn['XCN.2']);
  var giv = get(xcn['XCN.3']);
  var mid = get(xcn['XCN.4']);
  var deg = get(xcn['XCN.6']);
  var name = [giv, mid, fam].filter(Boolean).join(' ').replace(/\s+/g,' ').trim();
  var label = name || (fam || giv || '');
  var suffix = [];
  if (deg) suffix.push(deg);
  if (id)  suffix.push('(' + id + ')');
  return (label + (suffix.length ? ' ' + suffix.join(' ') : '')).trim();
}

function TCMDates(dischargeDateTime) {
    var TCM = { TcmSchedule1: null, TcmSchedule2: null };
    if (!dischargeDateTime) return TCM;

    var SimpleDateFormat = Packages.java.text.SimpleDateFormat;
    var Calendar         = Packages.java.util.Calendar;
    var TimeZone         = Packages.java.util.TimeZone;

    // Try both patterns (with T and with space)
    var patterns = ["yyyy-MM-dd'T'HH:mm:ss", "yyyy-MM-dd HH:mm:ss"];
    var parsed = null;

    for (var i = 0; i < patterns.length && parsed == null; i++) {
        var fmt = new SimpleDateFormat(patterns[i]);
        fmt.setLenient(false);
        // Optional: set a timezone if desired (comment out if you want server local time)
        // fmt.setTimeZone(TimeZone.getTimeZone("UTC"));
        try {
            parsed = fmt.parse(String(dischargeDateTime));
        } catch (e) {
            // try next pattern
        }
    }

    if (parsed == null) {
        logger.warn('Invalid DischargeDateTime for TCM schedule calculation: ' + dischargeDateTime);
        return TCM;
    }

    // Use Calendar to add days
    var cal1 = Calendar.getInstance();
    cal1.setTime(parsed);
    cal1.add(Calendar.DAY_OF_MONTH, 7);

    var cal2 = Calendar.getInstance();
    cal2.setTime(parsed);
    cal2.add(Calendar.DAY_OF_MONTH, 15);

    // Output format (ISO-like)
    var outFmt = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss");
    // Optional: keep same timezone as above if you set one
    // outFmt.setTimeZone(TimeZone.getTimeZone("UTC"));

    TCM.TcmSchedule1 = outFmt.format(cal1.getTime());
    TCM.TcmSchedule2 = outFmt.format(cal2.getTime());
    return TCM;
}

