function insertQueryBuilder(query) {
	var env = JSON.parse(configurationMap.get("settings"));
        var driver  = 'net.sourceforge.jtds.jdbc.Driver';
        var jdbcUrl = 'jdbc:jtds:sqlserver://he-sql-dev-eus2.database.windows.net:1433/he-healthcare-db;' +
                      'ssl=require;loginTimeout=30;';
        var user    = 'headmin';
        var pass    = 'HealthExt2025!';

        dbConn = DatabaseConnectionFactory.createDatabaseConnection(driver, jdbcUrl, user, pass);
    var result
    try {
//    	 logger.debug("query: " + query)
	result = dbConn.executeUpdate(query);
     dbConn.close();
    } finally {
        if (dbConn) {
            dbConn.close();
        }
        return result;
    }
}

function selectQueryBuilder(query) {
//    var DatabaseConnectionFactory = Packages.com.mirth.connect.server.userutil.DatabaseConnectionFactory;
    var dbConn = null;
    var rs = null;

    try {
        // If you keep creds in configurationMap settings, pull them here (optional)
        // var env = JSON.parse(configurationMap.get("settings") || "{}");

        // jTDS example (works with Azure SQL; Microsoft driver is recommended for prod)
        var driver  = 'net.sourceforge.jtds.jdbc.Driver';
        var jdbcUrl = 'jdbc:jtds:sqlserver://he-sql-dev-eus2.database.windows.net:1433/he-healthcare-db;' +
                      'ssl=require;loginTimeout=30;';
        var user    = 'headmin';
        var pass    = 'HealthExt2025!';

        dbConn = DatabaseConnectionFactory.createDatabaseConnection(driver, jdbcUrl, user, pass);

        rs = dbConn.executeCachedQuery(query);
        var rsmd = rs.getMetaData();
        var colCount = rsmd.getColumnCount();

        var rows = [];

        while (rs.next()) {
            var row = {};
            for (var i = 1; i <= colCount; i++) {
                var columnName = String(rsmd.getColumnName(i));
                var value = rs.getString(i); // String or null; preserves your desired representation
                row[columnName] = (value === null) ? null : String(value);
            }
            rows.push(row);
        }

        return rows; // e.g. [{"PatientKey":"4","TenantKey":"1",...}]
    } catch (err) {
        logger.debug('DB select error: ' + err);
//        logger.debug("Slect Error: " + query)
        return [];
    } finally {
        try { if (rs) rs.close(); } catch (e1) {}
        try { if (dbConn) dbConn.close(); } catch (e2) {}
    }
}

/**
 * Update an existing Encounter row with fields provided in `encounter`.
 * Match key: TenantKey + HospitalKey + PatientKey + VisitNumber
 * - Only non-null/meaningful values are updated (no overwriting with blanks).
 * - Automatically sets LastUpdatedUtc = SYSUTCDATETIME().
 */
function updateEncounter(encounter) {
  // ---------- helpers ----------
  function esc(val) { return (val == null) ? null : String(val).replace(/'/g, "''"); }
  function hasVal(v) { return !(v == null || String(v).trim() === ""); }
  function toSqlStr(val) { return hasVal(val) ? "'" + esc(String(val).trim()) + "'" : null; }
  function toSqlInt(val) {
    if (val == null || String(val).trim() === "") return null;
    var n = Number(val);
    return isNaN(n) ? null : String(Math.trunc(n));
  }
  function toSqlBit(val) {
    if (val === 0 || val === "0" || val === false || String(val).toLowerCase() === "false") return "0";
    if (val === 1 || val === "1" || val === true  || String(val).toLowerCase() === "true")  return "1";
    return null;
  }
  function toSqlDateTime(val) {
    if (!hasVal(val)) return null;
    var s = String(val).trim().replace('T',' ');
    return "'" + esc(s) + "'";
  }
  function add(assignments, column, sqlVal) {
    if (sqlVal !== null && sqlVal !== undefined) assignments.push("[" + column + "] = " + sqlVal);
  }

  // ---------- keys (required) ----------
  var whereParts = [];
  add(whereParts, "TenantKey", toSqlStr(encounter.TenantKey));
  add(whereParts, "HospitalKey", toSqlInt(encounter.HospitalKey));
  add(whereParts, "PatientKey", toSqlInt(encounter.PatientKey));
  add(whereParts, "AdmitDateTime", toSqlStr(encounter.AdmitDateTime));
  add(whereParts, "Location", toSqlStr(encounter.Location));
//  add(whereParts, "VisitNumber", toSqlStr(encounter.VisitNumber));

  if (whereParts.length !== 5) {
    throw "updateEncounter: missing key(s). Required: TenantKey, HospitalKey, PatientKey, Location";
  }

  // ---------- SET list (only what you pass) ----------
  var setParts = [];

  // Core timing
  //add(setParts, "AdmitDateTime",        toSqlDateTime(encounter.AdmitDateTime));
  add(setParts, "DischargeDateTime",    toSqlDateTime(encounter.DischargeDateTime));

  // Class/location/detail
  add(setParts, "PatientClass",         toSqlStr(encounter.PatientClass));
  //add(setParts, "Location",             toSqlStr(encounter.Location));
  //add(setParts, "AdmitType",            toSqlStr(encounter.AdmitType));
  //add(setParts, "AdmitSource",          toSqlStr(encounter.AdmitSource));
  add(setParts, "VisitStatus",          toSqlStr(encounter.VisitStatus));
  add(setParts, "Notes",                toSqlStr(encounter.Notes));

  // Provider display strings
  add(setParts, "AttendingDoctor",      toSqlStr(encounter.AttendingDoctor));
  add(setParts, "AdmittingDoctor",      toSqlStr(encounter.AdmittingDoctor));
  add(setParts, "PrimaryDoctor",        toSqlStr(encounter.PrimaryDoctor));

  // Message IDs
 // add(setParts, "AdmitMessageId",       toSqlStr(encounter.AdmitMessageId));
  add(setParts, "DischargeMessageId",   toSqlStr(encounter.DischargeMessageId));

  // Discharge / Diagnosis
  add(setParts, "DischargeDispositionCode", toSqlStr(encounter.DischargeDispositionCode));
  add(setParts, "PrincipalDiagnosisCode",   toSqlStr(encounter.PrincipalDiagnosisCode));

  add(setParts, "EventType",   toSqlStr(encounter.EventType));

  // Care transition / TCM
  var obsBit = toSqlBit(encounter.ObservationFlag); if (obsBit !== null) add(setParts, "ObservationFlag", obsBit);
  add(setParts, "HandoffAcknowledgedDate",  toSqlDateTime(encounter.HandoffAcknowledgedDate));
  add(setParts, "ReadmissionSourceEncKey",  toSqlInt(encounter.ReadmissionSourceEncKey));

  // Payer context
  add(setParts, "PayerName",                toSqlStr(encounter.PayerName));
  add(setParts, "MemberId",                 toSqlStr(encounter.MemberId));

  // TCM Schedule FKs
  add(setParts, "TCMSchedule1",                toSqlStr(encounter.TCMSchedule1));
  add(setParts, "TCMSchedule2",                 toSqlStr(encounter.TCMSchedule2));

  // Provider FKs
  add(setParts, "AttendingProviderKey",     toSqlInt(encounter.AttendingProviderKey));
  add(setParts, "AdmittingProviderKey",     toSqlInt(encounter.AdmittingProviderKey));
  add(setParts, "DischargeProviderKey",     toSqlInt(encounter.DischargeProviderKey));

  // Status
  var statusInt = toSqlInt(encounter.Status);
  if (statusInt !== null) add(setParts, "Status", statusInt);

  // Always touch audit column
  setParts.push("[LastUpdatedUtc] = SYSUTCDATETIME()");

  if (setParts.length === 0) {
    logger.warn("updateEncounter: nothing to update.");
    return null;
  }

  var updateQuery =
    "UPDATE [he].[Encounter] SET " + setParts.join(", ") +
    " WHERE " + whereParts.join(" AND ") + ";";

// logger.debug("SQL Update Query: " + updateQuery);
  var resp = updateQueryBuilder(updateQuery);
  return resp;
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateQueryBuilder(query) {
	var env = JSON.parse(configurationMap.get("settings"));
        var driver  = 'net.sourceforge.jtds.jdbc.Driver';
        var jdbcUrl = 'jdbc:jtds:sqlserver://he-sql-dev-eus2.database.windows.net:1433/he-healthcare-db;' +
                      'ssl=require;loginTimeout=30;';
        var user    = 'headmin';
        var pass    = 'HealthExt2025!';

        dbConn = DatabaseConnectionFactory.createDatabaseConnection(driver, jdbcUrl, user, pass);
    var result

    try {
        result = dbConn.executeUpdate(query); // rows affected
        return result;
    } catch (e) {
        logger.error('updateQueryBuilder error: ' + e);
        throw e;
    } finally {
        if (dbConn) {
            try { dbConn.close(); } catch (ignore) {}
        }
    }
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
/**
 * upsertCareTransition
 * --------------------
 * Creates or updates a CareTransition row when a discharge (ORU) message is received.
 * Match key: TenantKey + EncounterKey (one active CareTransition per Encounter)
 */
function upsertCareTransition(ct) {
  // ---------- helpers ----------
  function esc(val) { return (val == null) ? null : String(val).replace(/'/g, "''"); }
  function hasVal(v) { return !(v == null || String(v).trim() === ""); }
  function toSqlStr(val) { return hasVal(val) ? "'" + esc(String(val).trim()) + "'" : "NULL"; }
  function toSqlInt(val) {
    if (val == null || String(val).trim() === "") return "NULL";
    var n = Number(val);
    return isNaN(n) ? "NULL" : String(Math.trunc(n));
  }
  function toSqlBit(val) {
    if (val === 0 || val === "0" || val === false || String(val).toLowerCase() === "false") return "0";
    if (val === 1 || val === "1" || val === true  || String(val).toLowerCase() === "true")  return "1";
    return "NULL";
  }
  function toSqlDateTime(val) {
    if (!hasVal(val)) return "NULL";
    var s = String(val).trim().replace('T',' ');
    return "'" + esc(s) + "'";
  }

  // ---------- parameters ----------
  var TenantKey             = toSqlStr(ct.TenantKey);
  var EncounterKey          = toSqlInt(ct.EncounterKey);
  var PatientKey            = toSqlInt(ct.PatientKey);
  var HospitalKey           = toSqlInt(ct.HospitalKey);
  var VisitNumber           = toSqlStr(ct.VisitNumber);

  var FollowUpProviderKey   = toSqlInt(ct.FollowUpProviderKey);
  var FollowUpApptDateTime  = toSqlDateTime(ct.FollowUpApptDateTime);
  var CommunicationSentDate = toSqlDateTime(ct.CommunicationSentDate);
  var OutreachDate        = toSqlDateTime(ct.OutreachDate);
  var OutreachMethod      = toSqlStr(ct.OutreachMethod);
  var TcmSchedule1          = toSqlDateTime(ct.TcmSchedule1);
  var TcmSchedule2          = toSqlDateTime(ct.TcmSchedule2);

  var CareManagerUserKey    = toSqlInt(ct.CareManagerUserKey);
  var AssignedToUserKey     = toSqlInt(ct.AssignedToUserKey);
  var AssignedTeam          = toSqlStr(ct.AssignedTeam);
  var Status                = toSqlStr(ct.Status || "Open");
  var Priority              = toSqlStr(ct.Priority);
  var RiskTier              = toSqlStr(ct.RiskTier);
  var ReadmissionRiskScore  = (ct.ReadmissionRiskScore != null) ? String(ct.ReadmissionRiskScore) : "NULL";
  var ConsentConfirmed      = toSqlBit(ct.ConsentConfirmed);
  var PreferredLanguage     = toSqlStr(ct.PreferredLanguage);
  var OutreachAttempts      = toSqlInt(ct.OutreachAttempts || 0);
  var LastOutreachDate      = toSqlDateTime(ct.LastOutreachDate);
  var NextOutreachDate      = toSqlDateTime(ct.NextOutreachDate);
  var ContactOutcome        = toSqlStr(ct.ContactOutcome);
  var Notes                 = toSqlStr(ct.Notes);

  // ---------- build SQL ----------
  var src =
    "SELECT " +
    TenantKey             + " AS TenantKey, " +
    EncounterKey          + " AS EncounterKey, " +
    PatientKey            + " AS PatientKey, " +
    HospitalKey           + " AS HospitalKey, " +
    VisitNumber           + " AS VisitNumber, " +
    FollowUpProviderKey   + " AS FollowUpProviderKey, " +
    FollowUpApptDateTime  + " AS FollowUpApptDateTime, " +
    CommunicationSentDate + " AS CommunicationSentDate, " +
    OutreachDate          + " AS OutreachDate, " +
    OutreachMethod        + " AS OutreachMethod, " +
    TcmSchedule1          + " AS TcmSchedule1, " +
    TcmSchedule2          + " AS TcmSchedule2, " +
    CareManagerUserKey    + " AS CareManagerUserKey, " +
    AssignedToUserKey     + " AS AssignedToUserKey, " +
    AssignedTeam          + " AS AssignedTeam, " +
    Status                + " AS Status, " +
    Priority              + " AS Priority, " +
    RiskTier              + " AS RiskTier, " +
    ReadmissionRiskScore  + " AS ReadmissionRiskScore, " +
    ConsentConfirmed      + " AS ConsentConfirmed, " +
    PreferredLanguage     + " AS PreferredLanguage, " +
    OutreachAttempts      + " AS OutreachAttempts, " +
    LastOutreachDate      + " AS LastOutreachDate, " +
    NextOutreachDate      + " AS NextOutreachDate, " +
    ContactOutcome        + " AS ContactOutcome, " +
    Notes                 + " AS Notes";

  var merge =
    "MERGE he.CareTransition AS tgt \n" +
    "USING (" + src + ") AS src \n" +
    "  ON (tgt.EncounterKey = src.EncounterKey AND tgt.IsActive = 1)\n" +
    "WHEN MATCHED THEN UPDATE SET \n" +
    "  tgt.FollowUpProviderKey   = COALESCE(src.FollowUpProviderKey, tgt.FollowUpProviderKey),\n" +
    "  tgt.FollowUpApptDateTime  = COALESCE(src.FollowUpApptDateTime, tgt.FollowUpApptDateTime),\n" +
    "  tgt.CommunicationSentDate = COALESCE(src.CommunicationSentDate, tgt.CommunicationSentDate),\n" +
    "  tgt.OutreachDate        = COALESCE(src.OutreachDate, tgt.OutreachDate),\n" +
    "  tgt.OutreachMethod             = COALESCE(src.OutreachMethod, tgt.OutreachMethod),\n" +
    "  tgt.TcmSchedule1          = COALESCE(src.TcmSchedule1, tgt.TcmSchedule1),\n" +
    "  tgt.TcmSchedule2          = COALESCE(src.TcmSchedule2, tgt.TcmSchedule2),\n" +
    "  tgt.CareManagerUserKey    = COALESCE(src.CareManagerUserKey, tgt.CareManagerUserKey),\n" +
    "  tgt.AssignedToUserKey     = COALESCE(src.AssignedToUserKey, tgt.AssignedToUserKey),\n" +
    "  tgt.AssignedTeam          = COALESCE(src.AssignedTeam, tgt.AssignedTeam),\n" +
    "  tgt.Status                = COALESCE(src.Status, tgt.Status),\n" +
    "  tgt.Priority              = COALESCE(src.Priority, tgt.Priority),\n" +
    "  tgt.RiskTier              = COALESCE(src.RiskTier, tgt.RiskTier),\n" +
    "  tgt.ReadmissionRiskScore  = COALESCE(src.ReadmissionRiskScore, tgt.ReadmissionRiskScore),\n" +
    "  tgt.ConsentConfirmed      = COALESCE(src.ConsentConfirmed, tgt.ConsentConfirmed),\n" +
    "  tgt.PreferredLanguage     = COALESCE(src.PreferredLanguage, tgt.PreferredLanguage),\n" +
    "  tgt.OutreachAttempts      = COALESCE(src.OutreachAttempts, tgt.OutreachAttempts),\n" +
    "  tgt.LastOutreachDate      = COALESCE(src.LastOutreachDate, tgt.LastOutreachDate),\n" +
    "  tgt.NextOutreachDate      = COALESCE(src.NextOutreachDate, tgt.NextOutreachDate),\n" +
    "  tgt.ContactOutcome        = COALESCE(src.ContactOutcome, tgt.ContactOutcome),\n" +
    "  tgt.Notes                 = COALESCE(src.Notes, tgt.Notes),\n" +
    "  tgt.LastUpdatedUtc        = SYSUTCDATETIME()\n" +
    "WHEN NOT MATCHED THEN \n" +
    "  INSERT (TenantKey, EncounterKey, PatientKey, HospitalKey, VisitNumber,\n" +
    "          FollowUpProviderKey, FollowUpApptDateTime, CommunicationSentDate, OutreachDate, OutreachMethod,\n" +
    "          TcmSchedule1, TcmSchedule2, CareManagerUserKey, AssignedToUserKey, AssignedTeam, Status,\n" +
    "          Priority, RiskTier, ReadmissionRiskScore, ConsentConfirmed, PreferredLanguage,\n" +
    "          OutreachAttempts, LastOutreachDate, NextOutreachDate, ContactOutcome, Notes)\n" +
    "  VALUES (src.TenantKey, src.EncounterKey, src.PatientKey, src.HospitalKey, src.VisitNumber,\n" +
    "          src.FollowUpProviderKey, src.FollowUpApptDateTime, src.CommunicationSentDate, src.OutreachDate, src.OutreachMethod,\n" +
    "          src.TcmSchedule1, src.TcmSchedule2, src.CareManagerUserKey, src.AssignedToUserKey, src.AssignedTeam, src.Status,\n" +
    "          src.Priority, src.RiskTier, src.ReadmissionRiskScore, src.ConsentConfirmed, src.PreferredLanguage,\n" +
    "          src.OutreachAttempts, src.LastOutreachDate, src.NextOutreachDate, src.ContactOutcome, src.Notes);";

//logger.debug("upsertCareTransition SQL: " + merge);
  var resp = insertQueryBuilder(merge);
  return resp;
}

/**
 * Upsert Encounter into he.Encounter using MERGE
 * Match key: TenantKey + HospitalKey + VisitNumber
 * Will NOT overwrite existing non-null values with nulls (COALESCE).
 *
 * Expected fields in `encounter` (strings unless noted):
 * TenantKey, HospitalKey (int), PatientKey (int), VisitNumber,
 * AdmitDateTime, DischargeDateTime,
 * PatientClass, Location, AdmitType, AdmitSource,
 * VisitStatus, Notes,
 * AttendingDoctor, AdmittingDoctor, PrimaryDoctor,
 * AdmitMessageId, DischargeMessageId,
 * DischargeDispositionCode, PrincipalDiagnosisCode,
 * ObservationFlag (0|1),
 * HandoffAcknowledgedDate,
 * ReadmissionSourceEncKey (int),
 * PayerName, MemberId,
 * AttendingProviderKey (int), AdmittingProviderKey (int), DischargeProviderKey (int),
 * Status (int)
 */
function upsertEncounter(encounter) {
  // ---------- helpers ----------
  function esc(val) {
    return (val == null) ? null : String(val).replace(/'/g, "''");
  }
  function toSqlStr(val) {
    if (val == null) return "NULL";
    var s = String(val).trim();
    return s === "" ? "NULL" : "'" + esc(s) + "'";
  }
  function toSqlInt(val) {
    if (val == null || String(val).trim() === "") return "NULL";
    var n = Number(val);
    return isNaN(n) ? "NULL" : String(Math.trunc(n));
  }
  function toSqlBit(val) {
    if (val === true || String(val).toLowerCase() === "true" || val === 1 || String(val) === "1") return "1";
    if (val === 0 || val === false || String(val) === "0" || String(val).toLowerCase() === "false") return "0";
    return "NULL";
  }
  function toSqlDateTime(val) {
    // Accepts ISO-like strings 'YYYY-MM-DDTHH:MM:SS' or 'YYYY-MM-DD HH:MM:SS' or 'YYYY-MM-DD'
    if (val == null) return "NULL";
    var s = String(val).trim();
    if (s === "") return "NULL";
    // Replace 'T' with space for SQL Server
    s = s.replace('T', ' ');
    return "'" + esc(s) + "'";
  }

  // ---------- project to SQL literals ----------
  var TenantKey               = toSqlStr(encounter.TenantKey);
  var HospitalKey             = toSqlInt(encounter.HospitalKey);
  var PatientKey              = toSqlInt(encounter.PatientKey);
  var VisitNumber             = toSqlStr(encounter.VisitNumber);

  var AdmitDateTime           = toSqlDateTime(encounter.AdmitDateTime);
  var DischargeDateTime       = toSqlDateTime(encounter.DischargeDateTime);

  var PatientClass            = toSqlStr(encounter.PatientClass);
  var Location                = toSqlStr(encounter.Location);
  var AdmitType               = toSqlStr(encounter.AdmitType);
  var AdmitSource             = toSqlStr(encounter.AdmitSource);

  var VisitStatus             = toSqlStr(encounter.VisitStatus);
  var Notes                   = toSqlStr(encounter.Notes);

  var AttendingDoctor         = toSqlStr(encounter.AttendingDoctor);
  var AdmittingDoctor         = toSqlStr(encounter.AdmittingDoctor);
  var PrimaryDoctor           = toSqlStr(encounter.PrimaryDoctor);

  var AdmitMessageId          = toSqlStr(encounter.AdmitMessageId);
  var DischargeMessageId      = toSqlStr(encounter.DischargeMessageId);

  var DischargeDispositionCode= toSqlStr(encounter.DischargeDispositionCode);
  var PrincipalDiagnosisCode  = toSqlStr(encounter.PrincipalDiagnosisCode);

  var ObservationFlag         = toSqlBit(encounter.ObservationFlag);

  var HandoffAcknowledgedDate = toSqlDateTime(encounter.HandoffAcknowledgedDate);

  var ReadmissionSourceEncKey = toSqlInt(encounter.ReadmissionSourceEncKey);

  var PayerName               = toSqlStr(encounter.PayerName);
  var MemberId                = toSqlStr(encounter.MemberId);

  var AttendingProviderKey    = toSqlInt(encounter.AttendingProviderKey);
  var AdmittingProviderKey    = toSqlInt(encounter.AdmittingProviderKey);
  var DischargeProviderKey    = toSqlInt(encounter.DischargeProviderKey);

  var Status                  = toSqlInt(encounter.Status != null ? encounter.Status : 1); // default 1

  // ---------- source SELECT for MERGE ----------
  var src =
    "SELECT " +
    TenantKey               + " AS TenantKey, " +
    HospitalKey             + " AS HospitalKey, " +
    PatientKey              + " AS PatientKey, " +
    VisitNumber             + " AS VisitNumber, " +
    AdmitDateTime           + " AS AdmitDateTime, " +
    DischargeDateTime       + " AS DischargeDateTime, " +
    PatientClass            + " AS PatientClass, " +
    Location                + " AS Location, " +
    AdmitType               + " AS AdmitType, " +
    AdmitSource             + " AS AdmitSource, " +
    VisitStatus             + " AS VisitStatus, " +
    Notes                   + " AS Notes, " +
    AttendingDoctor         + " AS AttendingDoctor, " +
    AdmittingDoctor         + " AS AdmittingDoctor, " +
    PrimaryDoctor           + " AS PrimaryDoctor, " +
    AdmitMessageId          + " AS AdmitMessageId, " +
    DischargeMessageId      + " AS DischargeMessageId, " +
    DischargeDispositionCode+ " AS DischargeDispositionCode, " +
    PrincipalDiagnosisCode  + " AS PrincipalDiagnosisCode, " +
    ObservationFlag         + " AS ObservationFlag, " +
    HandoffAcknowledgedDate + " AS HandoffAcknowledgedDate, " +
    ReadmissionSourceEncKey + " AS ReadmissionSourceEncKey, " +
    PayerName               + " AS PayerName, " +
    MemberId                + " AS MemberId, " +
    AttendingProviderKey    + " AS AttendingProviderKey, " +
    AdmittingProviderKey    + " AS AdmittingProviderKey, " +
    DischargeProviderKey    + " AS DischargeProviderKey, " +
    Status                  + " AS Status";

  // ---------- MERGE ----------
  var merge =
    "MERGE he.Encounter AS tgt \n" +
    "USING (" + src + ") AS src \n" +
    "  ON (tgt.TenantKey = src.TenantKey \n" +
    "      AND tgt.HospitalKey = src.HospitalKey \n" +
    "      AND tgt.PatientKey = src.PatientKey \n" +
    "      AND tgt.AdmitDateTime = src.AdmitDateTime \n" +
    "      AND tgt.VisitNumber = src.VisitNumber)\n" +
    "WHEN MATCHED THEN UPDATE SET \n" +
    // Use COALESCE to avoid wiping values with NULL
    "  tgt.PatientKey              = COALESCE(src.PatientKey, tgt.PatientKey),\n" +
    "  tgt.AdmitDateTime           = COALESCE(src.AdmitDateTime, tgt.AdmitDateTime),\n" +
    "  tgt.DischargeDateTime       = COALESCE(src.DischargeDateTime, tgt.DischargeDateTime),\n" +
    "  tgt.PatientClass            = COALESCE(src.PatientClass, tgt.PatientClass),\n" +
    "  tgt.Location                = COALESCE(src.Location, tgt.Location),\n" +
    "  tgt.AdmitType               = COALESCE(src.AdmitType, tgt.AdmitType),\n" +
    "  tgt.AdmitSource             = COALESCE(src.AdmitSource, tgt.AdmitSource),\n" +
    "  tgt.VisitStatus             = COALESCE(src.VisitStatus, tgt.VisitStatus),\n" +
    "  tgt.Notes                   = COALESCE(src.Notes, tgt.Notes),\n" +
    "  tgt.AttendingDoctor         = COALESCE(src.AttendingDoctor, tgt.AttendingDoctor),\n" +
    "  tgt.AdmittingDoctor         = COALESCE(src.AdmittingDoctor, tgt.AdmittingDoctor),\n" +
    "  tgt.PrimaryDoctor           = COALESCE(src.PrimaryDoctor, tgt.PrimaryDoctor),\n" +
    "  tgt.AdmitMessageId          = COALESCE(src.AdmitMessageId, tgt.AdmitMessageId),\n" +
    "  tgt.DischargeMessageId      = COALESCE(src.DischargeMessageId, tgt.DischargeMessageId),\n" +
    "  tgt.DischargeDispositionCode= COALESCE(src.DischargeDispositionCode, tgt.DischargeDispositionCode),\n" +
    "  tgt.PrincipalDiagnosisCode  = COALESCE(src.PrincipalDiagnosisCode, tgt.PrincipalDiagnosisCode),\n" +
    "  tgt.ObservationFlag         = COALESCE(src.ObservationFlag, tgt.ObservationFlag),\n" +
    "  tgt.HandoffAcknowledgedDate = COALESCE(src.HandoffAcknowledgedDate, tgt.HandoffAcknowledgedDate),\n" +
    "  tgt.ReadmissionSourceEncKey = COALESCE(src.ReadmissionSourceEncKey, tgt.ReadmissionSourceEncKey),\n" +
    "  tgt.PayerName               = COALESCE(src.PayerName, tgt.PayerName),\n" +
    "  tgt.MemberId                = COALESCE(src.MemberId, tgt.MemberId),\n" +
    "  tgt.AttendingProviderKey    = COALESCE(src.AttendingProviderKey, tgt.AttendingProviderKey),\n" +
    "  tgt.AdmittingProviderKey    = COALESCE(src.AdmittingProviderKey, tgt.AdmittingProviderKey),\n" +
    "  tgt.DischargeProviderKey    = COALESCE(src.DischargeProviderKey, tgt.DischargeProviderKey),\n" +
    "  tgt.Status                  = COALESCE(src.Status, tgt.Status),\n" +
    "  tgt.LastUpdatedUtc          = SYSUTCDATETIME()\n" +
    "WHEN NOT MATCHED THEN \n" +
    "  INSERT (\n" +
    "    TenantKey, HospitalKey, PatientKey, VisitNumber,\n" +
    "    AdmitDateTime, DischargeDateTime,\n" +
    "    PatientClass, Location, AdmitType, AdmitSource,\n" +
    "    VisitStatus, Notes,\n" +
    "    AttendingDoctor, AdmittingDoctor, PrimaryDoctor,\n" +
    "    AdmitMessageId, DischargeMessageId,\n" +
    "    DischargeDispositionCode, PrincipalDiagnosisCode,\n" +
    "    ObservationFlag, \n" +
    "    HandoffAcknowledgedDate,\n" +
    "    ReadmissionSourceEncKey,\n" +
    "    PayerName, MemberId,\n" +
    "    AttendingProviderKey, AdmittingProviderKey, DischargeProviderKey,\n" +
    "    Status\n" +
    "  ) VALUES (\n" +
    "    src.TenantKey, src.HospitalKey, src.PatientKey, src.VisitNumber,\n" +
    "    src.AdmitDateTime, src.DischargeDateTime,\n" +
    "    src.PatientClass, src.Location, src.AdmitType, src.AdmitSource,\n" +
    "    src.VisitStatus, src.Notes,\n" +
    "    src.AttendingDoctor, src.AdmittingDoctor, src.PrimaryDoctor,\n" +
    "    src.AdmitMessageId, src.DischargeMessageId,\n" +
    "    src.DischargeDispositionCode, src.PrincipalDiagnosisCode,\n" +
    "    src.ObservationFlag, \n" +
    "    src.HandoffAcknowledgedDate,\n" +
    "    src.ReadmissionSourceEncKey,\n" +
    "    src.PayerName, src.MemberId,\n" +
    "    src.AttendingProviderKey, src.AdmittingProviderKey, src.DischargeProviderKey,\n" +
    "    src.Status\n" +
    "  );";

  //logger.debug("upsertEncounter MERGE:\n" + merge);
  var resp = insertQueryBuilder(merge);
  return resp;
}

/**
 * Upsert Patient into he.Patient using MERGE.
 * Match key: TenantKey + AssigningAuthority + MRN
 * Updates demographics, contact, payer, preferences, PCPProviderKey.
 *
 * Expects `patient` to include (strings unless noted):
 *  TenantKey, PatientIdExternal, AssigningAuthority, MRN, SSN,
 *  FamilyName, GivenName, DOB_TS (MM/DD/YYYY), Sex,
 *  Phone, Email, PreferredLanguage, CommunicationConsent (0|1),
 *  PayerName, MemberId,
 *  AddressLine1, City, State, PostalCode, Country,
 *  PCPProviderKey (int or null)
 */
function upsertPatient(patient) {
  // ---- helpers -------------------------------------------------------------
  function esc(val) {
    return (val == null) ? null : String(val).replace(/'/g, "''");
  }
  function toSqlStr(val) {
    if (val == null) return "NULL";
    var s = String(val).trim();
    return s === "" ? "NULL" : "'" + esc(s) + "'";
  }
  function toSqlInt(val) {
    if (val == null || String(val).trim() === "") return "NULL";
    var n = Number(val);
    return isNaN(n) ? "NULL" : String(Math.trunc(n));
  }
  function toSqlBit(val) {
    // accept 1/0/true/false/"true"/"false"
    if (val === true || String(val).toLowerCase() === "true") return "1";
    if (val === 1 || String(val) === "1") return "1";
    return "0";
  }
  function toSqlDateFromDobTs(mmddyyyy) {
    // input like "12/03/1975" or "" -> returns '1975-12-03' or NULL
    if (!mmddyyyy) return "NULL";
    var s = String(mmddyyyy).trim();
    if (!s) return "NULL";
    var m = s.match(/^(\d{1,2})\/(\d{1,2})\/(\d{4})$/);
    if (!m) return "NULL";
    var mm = m[1].padStart(2, '0');
    var dd = m[2].padStart(2, '0');
    var yyyy = m[3];
    return "'" + yyyy + "-" + mm + "-" + dd + "'";
  }

  // ---- source projection (handle null/empty) -------------------------------
  var TenantKey           = toSqlStr(patient.TenantKey);
  var PatientIdExternal   = toSqlStr(patient.PatientIdExternal || patient.MRN); // keep prior behavior: default to MRN
  var AssigningAuthority  = toSqlStr(patient.AssigningAuthority);
  var MRN                 = toSqlStr(patient.MRN);
  var SSN                 = toSqlStr(patient.SSN);
  var FamilyName          = toSqlStr(patient.FamilyName);
  var GivenName           = toSqlStr(patient.GivenName);
  var DOB                 = toSqlDateFromDobTs(patient.DOB_TS); // date
  var Sex                 = toSqlStr(patient.Sex);
  var Phone               = toSqlStr(patient.Phone);
  var Email               = toSqlStr(patient.Email);
  var PreferredLanguage   = toSqlStr(patient.PreferredLanguage);
  var CommunicationConsent= toSqlBit(patient.CommunicationConsent); // bit
  var PayerName           = toSqlStr(patient.PayerName);
  var MemberId            = toSqlStr(patient.MemberId);
  var AddressLine1        = toSqlStr(patient.AddressLine1);
  var City                = toSqlStr(patient.City);
  var State               = toSqlStr(patient.State);
  var PostalCode          = toSqlStr(patient.PostalCode);
  var Country             = toSqlStr(patient.Country);
  var PCPProviderKey      = toSqlInt(patient.PCPProviderKey); // int or NULL

  // ---- MERGE statement -----------------------------------------------------
  // Note: set LastUpdatedUtc = SYSUTCDATETIME() on update for audit
  var src = ""
    + "SELECT "
    + TenantKey            + " AS TenantKey, "
    + PatientIdExternal    + " AS PatientIdExternal, "
    + AssigningAuthority   + " AS AssigningAuthority, "
    + MRN                  + " AS MRN, "
    + SSN                  + " AS SSN, "
    + FamilyName           + " AS FamilyName, "
    + GivenName            + " AS GivenName, "
    + DOB                  + " AS DOB, "
    + Sex                  + " AS Sex, "
    + Phone                + " AS Phone, "
    + Email                + " AS Email, "
    + PreferredLanguage    + " AS PreferredLanguage, "
    + CommunicationConsent + " AS CommunicationConsent, "
    + PayerName            + " AS PayerName, "
    + MemberId             + " AS MemberId, "
    + AddressLine1         + " AS AddressLine1, "
    + City                 + " AS City, "
    + State                + " AS State, "
    + PostalCode           + " AS PostalCode, "
    + Country              + " AS Country, "
    + PCPProviderKey       + " AS PCPProviderKey";

  var merge =
    "MERGE he.Patient AS tgt \n" +
    "USING (" + src + ") AS src \n" +
    "ON (tgt.TenantKey = src.TenantKey \n" +
    "    AND tgt.AssigningAuthority = src.AssigningAuthority \n" +
    "    AND tgt.MRN = src.MRN)\n" +
    "WHEN MATCHED THEN UPDATE SET \n" +
    "    tgt.PatientIdExternal   = src.PatientIdExternal,\n" +
    "    tgt.SSN                 = src.SSN,\n" +
    "    tgt.FamilyName          = src.FamilyName,\n" +
    "    tgt.GivenName           = src.GivenName,\n" +
    "    tgt.DOB                 = src.DOB,\n" +
    "    tgt.Sex                 = src.Sex,\n" +
    "    tgt.Phone               = src.Phone,\n" +
    "    tgt.Email               = src.Email,\n" +
    "    tgt.PreferredLanguage   = src.PreferredLanguage,\n" +
    "    tgt.CommunicationConsent= src.CommunicationConsent,\n" +
    "    tgt.PayerName           = src.PayerName,\n" +
    "    tgt.MemberId            = src.MemberId,\n" +
    "    tgt.AddressLine1        = src.AddressLine1,\n" +
    "    tgt.City                = src.City,\n" +
    "    tgt.State               = src.State,\n" +
    "    tgt.PostalCode          = src.PostalCode,\n" +
    "    tgt.Country             = src.Country,\n" +
    "    tgt.PCPProviderKey      = src.PCPProviderKey,\n" +
    "    tgt.LastUpdatedUtc      = SYSUTCDATETIME()\n" +
    "WHEN NOT MATCHED THEN \n" +
    "    INSERT (\n" +
    "      TenantKey, PatientIdExternal, AssigningAuthority, MRN, SSN,\n" +
    "      FamilyName, GivenName, DOB, Sex, Phone, Email, PreferredLanguage,\n" +
    "      CommunicationConsent, PayerName, MemberId,\n" +
    "      AddressLine1, City, State, PostalCode, Country, PCPProviderKey\n" +
    "    ) VALUES (\n" +
    "      src.TenantKey, src.PatientIdExternal, src.AssigningAuthority, src.MRN, src.SSN,\n" +
    "      src.FamilyName, src.GivenName, src.DOB, src.Sex, src.Phone, src.Email, src.PreferredLanguage,\n" +
    "      src.CommunicationConsent, src.PayerName, src.MemberId,\n" +
    "      src.AddressLine1, src.City, src.State, src.PostalCode, src.Country, src.PCPProviderKey\n" +
    "    );";

//  logger.debug("upsertPatient MERGE:\n" + merge);
  var resp = insertQueryBuilder(merge);
  return resp;
}

/**
	This function format the Patient date of Birth string as yyyy/MM/dd

	@param {String} str - dob from Json object
	@return {String} return dob s yyyy/MM/dd
*/

function formatDate(str) {
    let dob = "";
    if (str.length > 7) {
        const year = str.substr(0, 4),
            day = str.substr(6, 2),
            month = str.substr(4, 2);
        //const D = new Date(y, m, d);
        dob = month + "/" + day + "/" + year;
    }

    return dob;
}

/**
	This function creates a Json object from Json message (contact object)

	@param {String} system - system from JSON message
	@param {String} use - use from JSON message
	@param {String} value - value from JSON message
	@return {String} return Json object
*/
function getContactsJsonObject(system, use, value) {
    return  {
        "system": system,
        "use": use,
        "value": value
    };
}

/**
	Create a full name from a first name, middle name, last name

	@param {String} firstName - First Name
	@param {String} middleName - Middle Name
	@param {String} lastName - Last Name
	@return {String} return description
*/
function getFullName(firstName, lastName, middleName) {
	   let fullName = firstName + " " + middleName + " " + lastName;
	   return fullName = fullName.replace(/\s\s+/g,' ');
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function getFullNameWithTitle(firstName, lastName, middleName, title) {
	   let fullName = firstName + " " + middleName + " " + lastName;
	   if(isNotEmpty(title)){
	   	fullName += ", " + title;
	   }
	   return fullName = fullName.replace(/\s\s+/g,' ');

}

